import {ChangeEvent, useCallback, useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setTodoList, addTodo, State } from '../features/todoList/TodoSlice';
import './styles/TodoList.css';

function TodoList() {
  const [inputValue, setInputValue] = useState('');

  const dispatch = useDispatch();
  const todoList = useSelector((state: State) => state.todoList);
  //  console.log(todoList)
  function handleAddTodo() {
    dispatch(addTodo(inputValue))
  }
  function handleInputChange(e: ChangeEvent<HTMLInputElement>) {
    setInputValue(e.target.value)
  }


  return (
    <>
    <h1>Raul's Todo List</h1>

    <div className="addTodoContainer">
        <input
         className="input"
         id="todo"
         type="text" 
         placeholder='Add your todo...'
         onChange={handleInputChange}
         />
         <button 
         onClick={handleAddTodo}
         className="addTodoButton">ADD TODO</button>
    </div>
      {todoList.map(todo => (
        <p key={todo.id}>{todo.task}</p>
      ))}
    </>
  )
}

export default TodoList;
