import {ChangeEvent, useCallback, useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setTodoList, addTodo, updateTodo, State } from '../features/todoList/TodoSlice';
import './styles/TodoList.css';
import { clickOptions } from '@testing-library/user-event/dist/click';

function TodoList() {
  const [inputValue, setInputValue] = useState('');
  const [priorityCriteria, setPriorityCriteria] = useState("High");

  const dispatch = useDispatch();
  const todoList = useSelector((state: State) => state.todoList);
  const sortCriteria = useSelector((state: State) => state.todoList);

  //  console.log(todoList)
  const handleAddTodo = useCallback(() => {
    dispatch(addTodo({
      task: inputValue,
      priorityCriteria,
      isCompleted: false
    }));
    setInputValue('');
  },[dispatch, inputValue, priorityCriteria])

  const handleInputChange = useCallback((e: ChangeEvent<HTMLInputElement>) =>{
    const inputValue = e.target.value
    setInputValue(inputValue)
  }, []);

  const handlePriorityCriteria = useCallback((e: ChangeEvent<HTMLSelectElement>) => {
    setPriorityCriteria(e.target.value)
  },[])

  const handleToggleTodo = useCallback((TodoId: string) => {
    const todoToToggle = todoList.find(todo => todo.id === TodoId);
    if(todoToToggle) {
      dispatch(updateTodo({
        id: TodoId,
        isCompleted: !todoToToggle.isCompleted
       }))
    }
  },[dispatch, todoList])

  return (
    <>
    <h1>Raul's Todo List</h1>

    <div className="addTodoContainer">
        <input
         className="input"
         id="todo"
         type="text"
         value={inputValue} 
         placeholder='Add your todo...'
         onChange={handleInputChange}
         />

          <select 
            value={priorityCriteria}
            onChange={handlePriorityCriteria}
          >
            <option value="High">High</option>
            <option value="Medium">Medium</option>
            <option value="Low">Low</option>
          </select>

         <button 
         onClick={handleAddTodo}
         className="addTodoButton">ADD TODO</button>
    </div>
      {todoList.map(todo => (
        <div key={todo.id}>
        <p>TASK: {todo.task}</p>
        <p>Priority: {todo.priorityCriteria}</p>
        <button 
          onClick={() => handleToggleTodo(todo.id)}
        >{todo.isCompleted ? 'Mark Incomplete' : 'Mark Complete'}</button>
        <p>{todo.isCompleted ? 'Completed' : 'Pending'}</p>
        </div>

      ))}
    </>
  )
}

export default TodoList;
