import {ChangeEvent, useCallback, useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setTodoList, addTodo, updateTodo, State } from '../features/todoList/TodoSlice';
import './styles/TodoList.css';

function TodoList() {
  const [inputValue, setInputValue] = useState('');
  const [priorityCriteria, setPriorityCriteria] = useState('');
  const [isCompleted, setIsCompleted] = useState(false);

  const dispatch = useDispatch();
  const todoList = useSelector((state: State) => state.todoList);
  const sortCriteria = useSelector((state: State) => state.todoList);
  //  console.log(todoList)
  function handleAddTodo() {
    dispatch(addTodo({
      task: inputValue,
      priorityCriteria,
      isCompleted
    }))
  }

  function handleInputChange(e: ChangeEvent<HTMLInputElement>) {
    const inputValue = e.target.value
    setInputValue(inputValue)
  }
  function handlePriorityCriteria(e: ChangeEvent<HTMLSelectElement>) {
    setPriorityCriteria(e.target.value)
  }


  return (
    <>
    <h1>Raul's Todo List</h1>

    <div className="addTodoContainer">
        <input
         className="input"
         id="todo"
         type="text" 
         placeholder='Add your todo...'
         onChange={handleInputChange}
         />

          <select 
            onChange={handlePriorityCriteria}>
            <option value="High">High</option>
            <option value="Medium">Medium</option>
            <option value="Low">Low</option>
          </select>

         <button 
         onClick={handleAddTodo}
         className="addTodoButton">ADD TODO</button>
    </div>
      {todoList.map(todo => (
        <div key={todo.id}>
        <p>TASK: {todo.task}</p>
        <p>Priority: {todo.priorityCriteria}</p>
        <p>Todo is Completed: {todo.isCompleted}</p>
        </div>

      ))}
    </>
  )
}

export default TodoList;
