import { createSlice } from '@reduxjs/toolkit';
import { v4 as uuidv4 } from 'uuid';

interface Todo {
    id: string;
    task: string;
    isCompleted: boolean;
    priorityCriteria: string;
}
interface State {
    todoList: Todo[],
    sortCriteria: string,
}

const initialState: State = {
    todoList: [],
    sortCriteria: 'All'
}

export const TodoSlice = createSlice({
    name: 'todo',
    initialState,
    reducers: {
        setTodoList: (state,action) => {
            state.todoList = action.payload;
        },
        addTodo: (state, action) => {
            state.todoList.push({
              id: uuidv4(),
              task: action.payload.task,
              priorityCriteria: action.payload.priorityCriteria,
              isCompleted: false,
            });
        },
        sortTodo: (state, action) => {
            state.sortCriteria = action.payload;
        },
        updateTodo: (state, action) => {
            const { id, task } = action.payload;
            const index = state.todoList.findIndex(todo => todo.id === id);
            if (index !== - 1) {
                state.todoList[index] = {
                    ...state.todoLIst[index],
                 }
            }

        },
        toggleTodo: (state, action) => {
            const {id, isCompleted} = action.payload;
            const index = state.todoList.findIndex(todo => todo.id === id);
            state.todoList[index].isCompleted = !state.todoList[index].isCompleted;
        }
    },
})
export const { setTodoList, addTodo, sortTodo, updateTodo, toggleTodo } = TodoSlice.actions
export default TodoSlice.reducer;
